import React, { Component } from 'react';

class DataTable extends Component {
    handleDelete = (e) => {
        e.preventDefault();
        console.log(e.target.value); //will give you the value continue
        this.props.nextStep();
   }
    render() {
        return (
            <tr>
                <td>
                    {this.props.obj.recipeName}
                </td>
                <td>
                    {this.props.obj.description}
                </td>
                <td>
                    {this.props.obj.instructions}
                </td>
                <td>
                    {this.props.obj.ingredients}
                </td>
                <td>
                    {this.props.obj.cooktime}
                </td>
                <td>
                    {this.props.obj.servings}
                </td>
                <td>
                    {this.props.obj.difficulty}
                </td>
                <td>
                    {this.props.obj.calories}
                </td>
                <td>
                <button value={this.props.obj.Id} onClick={e => this.handleDelete()}>
                    Delete
                </button>
                </td>
            </tr>
        );
    }
}

export default DataTable;
