import React, { Component, useState } from 'react';
import { Button, Form, FormGroup, FormControl, Col, Checkbox, ControlLabel } from 'react-bootstrap';
import "./CreateRecipe.css";
import Routes from "../Routes";
import axios from 'axios';
import DataTable from './data-table';

class CreateRecipe extends Component {
    constructor(props) {
        super(props);
        this.state = { recipeCollection: [] };
    }

    componentDidMount() {
        axios.get('http://localhost:5000/api/recipes')
            .then(res => {
                this.setState({ recipeCollection: res.data });
            })
            .catch(function (error) {
                console.log(error);
            })
    }

    dataTable() {
        return this.state.recipeCollection.map((data, i) => {
            return <DataTable obj={data} key={i} />;
        });
    }

    render() {
        return (
            <div className="wrapper-recipe">
                <div className="container">
                    <table className="table table-striped table-dark">
                        <thead className="thead-dark">
                            <tr>
                                <td>Name</td>
                                <td>Description</td>
                                <td>Instructions</td>
                                <td>Ingredients</td>
                                <td>Cook Time</td>
                                <td>Servings</td>
                                <td>Difficulty</td>
                                <td>Calories</td>
                            </tr>
                        </thead>
                        <tbody>
                            {this.dataTable()}
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}
